const [quantity, setQuantity] = useState(cartItem.map(item => 1));
const [disable, setDisable] = useState(cartItem.map(item => false));
const handleIncrement = (index) => {
  quantity[index]++;
    setQuantity([...quantity]);
    disable[index] = false;
    setDisable([...disable]);

};


const handleDecrement = (index) => {
  //setQuantity(prevCount => prevCount - 1);
  if (quantity[index] > 1) {
    disable[index] = false;
    setDisable([...disable]);
    quantity[index]--;
    setQuantity([...quantity]);
  } else {
    quantity[index] = 1;
    setQuantity([...quantity]);
    disable[index] = true;
    setDisable([...disable]);
  }

};
  /////////////////////
  const DeleteItem = async (id) => {
      var config = {
      method: 'Delete',
      url: `http://localhost:8000/api/user/cartItem/${id}`,
      headers: { 
        'Authorization': `Bearer ${cookie}`, 
        'Content-Type': 'application/x-www-form-urlencoded'
      }};
        axios(config)
        .then(res => {
          console.log(res)
          setCartItem(cartItem.filter(item => item.id !== id))    
        }).catch(err => {
          console.log(err)
        })
        setLoading(false)
  }
    const columns = [
        {
            field: 'image',
            headerName: 'Picture',
            width: 120,
            sortable: false,
            renderCell: (params) => (
                <img style={{width: '1', height: '1'}} alt="Remy Sharp" src= {`http://localhost:8000/storage/${params.row.image}`}/>
            ),
        },
        { field: 'name', headerName: 'name', width: 130 },
  { field: 'bottle_size', headerName: 'bottle_size', width: 130 },
  { field: 'price', headerName: 'price', width: 130 },
  { field: 'quantity', headerName: 'quantity', width: 130,
        renderCell:(params)=>(
          cartItem.map((cartItem,i)=>{
            <div key={i}>
            <div>{cartItem.quantity}</div>
    <ButtonGroup size="small" aria-label="small outlined button group">
        <Button  onClick={handleIncrement(i)}>+</Button>
         <Button disabled>{quantity[i]}</Button>
       <Button   disabled={disable[i]} onClick={handleDecrement(i)}>-</Button>
      </ButtonGroup>
          </div>


          })
    ),

},

      {
        field: 'delete',
        headerName: 'Remove',
        sortable: false,
        renderCell: (params) => (
            <Button onClick={()=> DeleteItem(params.row.id)} style={{backgroundColor: '#F76363'}} variant="contained" size="small" alt="Remy Sharp">
                Remove
            </Button>
        ),
      },


    ];
