const handleClick = (event) => {
  setAnchorEl(event.currentTarget);


};
    console.log(HandleEdit)
    if(Loading){
      return (
        <div>
            <Backdrop className={classes.backdrop} open={true}>
                <CircularProgress color="inherit" />
            </Backdrop>
        </div>
      )
    }
    else {
  return (
      <>
    <p>Your Items</p>  
    <div className={classes.manageItems} >
    <div className={classes.ItemsTable} >
    {<span style={display}>{message}</span>}
      <DataGrid rows={cartItem} columns={columns} pageSize={8} width="250px"/>
    </div>
    </div>
    <div>Total Price:{overall_price}LBP</div>
    <Button className="button-profile"  style={{ alignSelf:"center", width:"30%", color:"black", backgroundColor:"lightgray" }} edge="start" aria-label="menu" aria-haspopup="true" onClick={handleClick} >
             Checkout
             </Button>
    </>
  );
    }
}


export default Cart;
