import React, { useState, useEffect } from 'react';
import './FakeForm.scss';
import axios from 'axios'

function FakeForm(){
  const [email, setEmail] = useState('')
  const [name, setName] = useState('')
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [message, setMessage] = useState('')



  /* const handler = e => {
    setName(e.target.value)
    setEmail(e.target.value)
    setTitle(e.target.value)
    setContent(e.target.value)
  } */


  const submitForm = async e => {
    e.preventDefault()

    const data = new FormData();

    const response = await axios.post(`http://localhost:5000/api/messages`, {
      name: data.name,
      title: data.title,
      email: data.email,
      content: data.content
      })
        .then(res => {
          setMessage('Message Sent Successfully')
          console.log(res,"messaggeeeee")
        })
      .catch((error) => {
        if(error.response){
          setMessage('Message Was not sent Successfully')

        }
      })


  }





  return (
    <form onSubmit={submitForm}>
    <div className="input-areas-contol">
      <input
        className='footer-input'
        name='name'
        type='name'
        placeholder='Name'
        onChange={handler}
        value={name}
      />
      <input
        className='footer-input'
        name='email'
        type='email'
        placeholder='Email'
        onChange={handler}
        value={email}
      />
      <input
        className='footer-input'
        name='title'
        type='title'
        placeholder='Subject'
        onChange={handler}
        value={title}
      />
      <input
        className='footer-input'
        name='content'
        type='content'
        placeholder='Message'
        onChange={handler}
        value={content}
      />
    </div>
    <div className="input-areas-contol">
      <button type="submit" style={{ width: "50%" }} className='btn-link'>
       Send
  </button>
    </div>
    <div className="input-areas-control">
      <p>{message}</p>
    </div>
  </form>
  )
};

export default FakeForm;
