// export default Cart
import React, { useState, useEffect } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import { makeStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import CookieService from '../Service/CookieService';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import axios from 'axios';
import 'reactjs-popup/dist/index.css';
import { useForm } from "react-hook-form";
import { green } from '@material-ui/core/colors';
import { blue } from '@material-ui/core/colors';
function Cart() {
  const { register , handleSubmit, reset } = useForm()
  const [Items, setItems] = useState([]);
  const [Loading, setLoading] = useState(true)
  const [Editing, setEditing] = useState(false)
  const [display, setdisplay] = useState({display: 'None', color: 'red' });
  const [message, setmessage] = useState("none");
  const [Item, setItem] = useState([]);
  const name=[];
  const[cartItem,setCartItem]=useState([])
  const cartItems=[];
    const useStyles = makeStyles((theme) => ({
      formm: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
      },
      rate:{
        display: 'flex',
        justifyContent: 'center',
      },
      input:{
        display: 'flex',
        justifyContent: 'center',
        marginBottom:'1%',
        marginTop:'1%'
      },
      submit: {
        marginLeft:'1%',
        width: "10%",
        color: theme.palette.getContrastText(green[500]),
        backgroundColor: blue['#94aabe'],
        '&:hover': {
          backgroundColor: blue['#003366'],
        },
      },
        ItemsTable: {
          color: 'green',
          position: 'relative',
          zIndex: '0',
          width: '70%',
          height: '70vh',
          marginTop: '1%',
          marginLeft: '0%',

        },
        backdrop: {
          zIndex: theme.zIndex.drawer + 1,
          color: 'green',
        },
        manageItems : {
          /* backgroundColor: "rgba(116, 255, 116, 0.145)", */
          height : '86vh',
        }
      }));
      const classes = useStyles();
      const cookie = CookieService.get('Bearer');



   const HandleEdit = () => {
      setEditing(!Editing)
    }


    useEffect( () => {
      setLoading(false)
      var configg = {
        method: 'get',
        url: 'http://localhost:8000/api/user/cartItem',
        headers: { 
          'Authorization': `Bearer ${cookie}`, 
          'Content-Type': 'application/x-www-form-urlencoded'
        }};
          axios(configg)
          .then(res => {
                  console.log(res.data[0],"asdasd");

                setCartItem(res.data[0].map((item, index)=>
                    {return {
                        ...item[0],
                        // delete: {id: item.id},
                    }}));

                setItem(res.data[1])
                    setLoading(false)
          }).catch(err => {
            console.log(err.request)
          })
    },[]);
    console.log(cartItem,"ballout")
    console.log(Item,"karamel")


    const DeleteItem = async (id) => {
      var config = {
      method: 'Delete',
      url: `http://localhost:8000/api/user/cartItem/${id}`,
      headers: { 
        'Authorization': `Bearer ${cookie}`, 
        'Content-Type': 'application/x-www-form-urlencoded'
      }};
        axios(config)
        .then(res => {
          console.log(res)
          setCartItem(cartItem.filter(item => item.id !== id))    
        }).catch(err => {
          console.log(err)
        })
        setLoading(false)
  }
    const columns = [
      //   {
      //       field: 'image',
      //       headerName: 'Picture',
      //       width: 120,
      //       sortable: false,
      //       renderCell: (params) => (
      //           <img style={{width: '1', height: '1'}} alt="Remy Sharp" src= {`http://localhost:8000/storage/${params.row.image}`}/>
      //       ),
      //   },
      // {
      //   field: 'name',
      //   headerName: 'Name',
      //   width: 140,
      //   valueGetter: ({params}) =>
      //     `${params.getValue('name') || ''}`,
      // },

      {
        field: 'bottle_size',
        headerName: 'Size:',
        width: 120,
        valueGetter: (params) =>
          `${params.getValue(Item.bottle_size) || ''}`,
      },
      {
        field: 'price',
        headerName: 'Price:',
        width: 120,
        valueGetter: (params) =>
          `${params.getValue('price') || ''}`,
      },
      {
        field: 'quantity',
        headerName: 'quantity:',
        width: 120,
        valueGetter: (params) =>
          `${params.getValue('quantity') || ''}`,
      },
      {
        field: 'delete',
        headerName: 'Delete',
        sortable: false,
        renderCell: (params) => (
            <Button onClick={()=> DeleteItem(params.row.id)} style={{backgroundColor: '#F76363'}} variant="contained" size="small" alt="Remy Sharp">
                Delete
            </Button>
        ),
      },

    ];
    console.log(HandleEdit)
    if(Loading){
      return (
        <div>
            <Backdrop className={classes.backdrop} open={true}>
                <CircularProgress color="inherit" />
            </Backdrop>
        </div>
      )
    }
    else {
  return (
      <>
    <p>Your Items</p>  
    <div className={classes.manageItems} >
    <div className={classes.ItemsTable} >
    {<span style={display}>{message}</span>}
      <DataGrid rows={Item} columns={columns} pageSize={8} width="250px"/>
    </div>
    </div>
    </>
  );
    }
}


export default Cart;
