import React from 'react';
import { IconContext } from 'react-icons';
import * as FaIcons from 'react-icons/fa';
import { FcMenu } from "react-icons/fc";
import CookieService from '../Service/CookieService';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';
import './Profile.css';
import { Link } from 'react-router-dom';
import Cart from '../Cart/Cart';
import Order from '../Orders/Order';



export default function Profile() {
  const [Users, setUsers] = useState([])
  const [Loading, setLoading] = useState(true)
  const [Editing, setEditing] = useState(false)
  const [UserData, setUserData] = useState({})
  const [display, setdisplay] = useState({display: 'None', color: 'red' });
  const [message, setmessage] = useState("none");
  const cookie = CookieService.get('Bearer');



  const HandleEdit = () => {
     setEditing(!Editing)
   }

   useEffect( () => {
     setLoading(true)
     var config = {
       method: 'get',
       url: 'http://localhost:8000/api/admin/user/show',
       headers: { 
         'Authorization': `Bearer ${cookie}`, 
         'Content-Type': 'application/x-www-form-urlencoded'
       }};
         axios(config)
         .then(res => {
                 setUsers(res.data.map((item, index)=>  
                   {return {
                       ...item,
                       delete: {id: item.id},
                       edit: {id: item.id, index: index },
                       /* image   : linkkk + item.picture, */
                   }}));
                   setLoading(false)
         }).catch(err => {
           console.log(err.request)
         })
   },[]);





  const av = CookieService.get('av');

  function logout(){
    const cookie = CookieService.get('Bearer');
    var config = {
      method: 'post',
      url: 'http://localhost:8000/api/user/logout',
      headers: { 
        'Authorization': `Bearer ${cookie}`, 
        'Content-Type': 'application/x-www-form-urlencoded'
      }};

        axios(config)
        .then(res => {
           CookieService.remove('Bearer');
           CookieService.remove('av');
           window.location.replace("/")
        }).catch(err => {
           console.log(err)
           CookieService.remove('Bearer');
           CookieService.remove('av');
           window.location.replace("/")

        })

  }
    return (
        <div>
          <div class="profile">
          <Button onClick={()=> logout() } className="logoutbutton">
              Logout
            </Button>
          </div>

            <div class="flex-container">
             <div class="flex-item-left">
             <Cart />
             <div className="buttons">
             <Button className="buttonn"  style={{ alignSelf:"center", width:"50%", color:"black", backgroundColor:"lightgray" }} edge="start" aria-label="menu" aria-haspopup="true" /* onClick={() => ()} */>
             Checkout
             </Button>
             </div>
             <div className="buttons">
               <Link to="/">
             <Button className="buttonn"  style={{ alignSelf:"center", width:"50%", color:"black", backgroundColor:"lightgray" }} edge="start" aria-label="menu" aria-haspopup="true" /* onClick={() => () } */>
             Continue Shopping
             </Button>
             </Link>
            </div>
             </div>

             <div class="flex-item-right">
               <Order />

             </div>
            </div>
        </div>
    )
}
