import React from 'react';
import './Layout.scss';

import Footer from '../components/UI/Footer/Footer';
///////// navbar
import SidebarHome from '../containers/Header/SidebarHome/SidebarHome.js'
import items from '../containers/Header/SidebarHome/SidebarDataHome.js'
import NavbarHome from '../containers/Header/NavbarHome/NavbarHome.js'
import useVisible from '../containers/Service/useVisible'


const { ref, ref2, isVisible, setIsVisible } = useVisible(false);


const CheckSidebar = () => {
    setIsVisible(!isVisible);
};



function App() {


  return (
  <>
    <NavbarHome items={items} CheckSidebar={CheckSidebar}  isVisible={isVisible} forwardedRef={ref2}/>
    <SidebarHome items={items}  CheckSidebar={CheckSidebar} forwardedRef={ref} isVisible={isVisible}/>
    <main className="main-content">
      {props.children}
    </main>
    <Footer />
  </>
   );
  }


export default App;
