import './Layout.scss';

import Footer from '../components/UI/Footer/Footer';
import NavbarHome from '../containers/Header/NavbarHome/NavbarHome';


const { ref, ref2, isVisible, setIsVisible } = useVisible(false);


const CheckSidebar = () => {
    setIsVisible(!isVisible);
};



const Layout = (props) => (



  <>
        <NavbarHome items={items} CheckSidebar={CheckSidebar}  isVisible={isVisible} forwardedRef={ref2}/>
        <SidebarHome items={items}  CheckSidebar={CheckSidebar} forwardedRef={ref}
                isVisible={isVisible}/>
    <main className="main-content">
      {props.children}
    </main>
    <Footer />
  </>
);

export default Layout;
