import React, { Component, Fragment, useState } from 'react';
import { Switch, Route, Redirect, withRouter,  Link } from 'react-router-dom';


// COMPONENTS
import Layout from './layout/Layout';
import Contact from './containers/Contact/Contact';
import Product from './containers/ProductList/Product';
import Offers from './containers/OfferList/Offers';
import Featured from './containers/FeaturedList/Featured';
import HomePage from './containers/HomePage/HomePage';

// admin
import Protection from './Dashboard/loginPages/Protection';
import Dashboard from './Dashboard/Dashboard';
import Login from './Dashboard/loginPages/login.js';

////////
import Header from './components/Header';
import Main from './components/Main';
import Basket from './components/Basket';




function App() {

 /*  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin; */

  const { products } = data;
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };
  const onRemove = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };

    return (
      <Fragment>
        {/* <div className="grid-container">
        <header className="header">
          <div className="brand">
            <Link to="/">Kataleya</Link>
          </div>
          <div className="navbarr">
            <ul>
            <li>
            <Link to="/productlist">Perfumes</Link>
            </li>
            <li>
            <Link to="/featured">Featured</Link>
            </li>
            <li>
            <Link to="/offers">Offers</Link>
            </li>
            <li>
            <Link to="/contact">Contact</Link>
            </li>
            </ul>
          </div>
          <div className="header-links">
            <a href="cart">Cart</a>
            {userInfo ? (
              <Link to="/profile">{userInfo.name}</Link>
            ) : (
              <Link to="/signin">Sign In</Link>
            )}

          </div>
        </header>
      </div> */}

      <Header countCartItems={cartItems.length}></Header>
        <Switch>
            <Protection path="/admin" >
                <Dashboard /* pic={pic} */ />
            </Protection>
                <Route  path='/kataleya-admin'>
                  <Login /* picsetting={picsetting} */ />
                </Route>
          <Layout>
            <Route path="/contact" component={Contact} />
            <Route path="/productlist" component={Product} />
            <Route path="/offers" component={Offers} />
            <Route path="/featured" component={Featured} />
            <Route path="/" exact component={HomePage} />
            <Redirect to="/" />
          </Layout>
        </Switch>


      </Fragment>
    );
  }


export default App;
