import React, { useState, useEffect } from 'react';
import './FakeForm.scss';

function fakeForm(){
  const [email, setEmail] = useState('')
  const [name, setName] = useState('')
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')


  const handler = e => {
    setName(e.target.value)
    setEmail(e.target.value)
    setTitle(e.target.value)
    setContent(e.target.value)
  }


  const Auth = async (data) => {

    axios.post('http://localhost:8000/api/messages',
   {
     name: data.name,
     phone: data.phone,
     email: data.email,
     content: data.content

   })
   .then(res => {
     CookieService.set('Bearer ', res.data.access_token, { path: "/", 'max-Age': res.data.expires_in})
     CookieService.set('av ', res.data.av, { path: "/", 'max-Age': res.data.expires_in})
   })
 .catch((error) => {
   if(error.response){
     /* setmessage(Object.entries(error.response.data.error).map((item, index) => " " + item[1] + " ")) */
     setdisplay('inline');
   }else {
     setmessage("N e t w o r k  E r r o r")
     setdisplay('inline');
   }
 })
}





  return (
  <form className="questions-form">
    <input type="text" placeholder="Name" />
    <input type="text" placeholder="Email" />
    <input type="text" placeholder="Subject" />
    <textarea placeholder="Message"></textarea>
    <button>Send</button>
  </form>
  )
};

export default fakeForm;
