import ButtonGroup from "@material-ui/core/ButtonGroup";
function Cart(props) {
  const [Loading, setLoading] = useState(true)
  const [Editing, setEditing] = useState(false)
  const [display, setdisplay] = useState({display: 'None', color: 'red' });
  const [message, setmessage] = useState("none");
  const[cartItem,setCartItem]=useState([]);
  const [result,setResult]=useState("");
  const [overallPrice,setOverallPrice]=useState("");
  const[Items,setItems]=useState([]);

  function getArraySum(a){
    var total=0;
    for(var i in a) { 
        total += a[i];
    }
    return total;
}
    const useStyles = makeStyles((theme) => ({
      formm: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
      },
      rate:{
        display: 'flex',
        justifyContent: 'center',
      },
      input:{
        display: 'flex',
        justifyContent: 'center',
        marginBottom:'1%',
        marginTop:'1%'
      },
      submit: {
        marginLeft:'1%',
        width: "10%",
        color: theme.palette.getContrastText(green[500]),
        backgroundColor: blue['#94aabe'],
        '&:hover': {
          backgroundColor: blue['#003366'],
        },
      },
        ItemsTable: {
          color: 'green',
          position: 'relative',
          zIndex: '0',
          width: '70%',
          height: '70vh',
          marginTop: '1%',
          marginLeft: '0%',

        },
        backdrop: {
          zIndex: theme.zIndex.drawer + 1,
          color: 'green',
        },
        manageItems : {
          /* backgroundColor: "rgba(116, 255, 116, 0.145)", */
          height : '86vh',
        }
      }));
      const classes = useStyles();
      const cookie = CookieService.get('Bearer');
   const HandleEdit = () => {
      setEditing(!Editing)
    }

    useEffect( () => {
      setLoading(false)
      var configg = {
        method: 'get',
        url: 'http://localhost:8000/api/user/cartItem',
        headers: { 
          'Authorization': `Bearer ${cookie}`, 
          'Content-Type': 'application/x-www-form-urlencoded'
        }};
           axios(configg)
          .then(res => {
                // console.log(res.data,"asdasd");
              setCartItem(res.data)
              setResult(cartItem.map(({ price,quantity }) =>Number(price*quantity)))
              setOverallPrice(getArraySum(result))

          }).catch(err => {
            console.log(err.request)
          })

    },[]);
