import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Button } from "@material-ui/core";
import {Menu, MenuItem} from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import { useForm } from "react-hook-form";
import './Cards.css';
import Basket from '../../components/Basket';


const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 60,
  },
  tablecell: {
    fontSize: '10pt'
},
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',

  },

  image: {

  },

  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
}));

export default function ComplexGrid(props) {
const { register , handleSubmit, reset } = useForm();
const { id, image, name, description, is_offer} = props.details
const {rate}=props
const {item_attributes}=props



  const [price, setPrice] = useState(item_attributes[0].price)
  const [offer, setOffer] = useState(item_attributes[0].offer_price)



  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const handleClose = () => {
        setAnchorEl(null);
      };
  const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
      };

const handleChangeSize = (event) => {
  const pickedAttributes= (item_attributes.filter((item) => item.id == event.target.value))

    setPrice(pickedAttributes[0].price)
    setOffer(pickedAttributes[0].offer_price)

    };
////////// shoping cart

  const [cartItems, setCartItems] = useState([]);
  const qty =[]
  const onAdd = () => {
    const exist = cartItems.find((x) => x.id === id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { qty: 1 }]);
    }
  };
  const onRemove = () => {
    const exist = cartItems.find((x) => x.id === id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };


  const itemsPrice = cartItems.reduce((a, c) => a + c.qty * c.price, 0);
  const shippingPrice = itemsPrice < 300000 ? 0 : 5000;
  const totalPrice = itemsPrice + shippingPrice;
  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item className={classes.image}>
          <img className={classes.img} alt="complex" src= {`http://localhost:8000/storage/${image}`} />
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
              <Typography  style={ {fontWeight:"bold"}} variant="body1" gutterBottom>
                  {name}
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {description}
                </Typography>


              </Grid>
              <div>
           {/*  <Button style={{ alignSelf:"center", width:"100%", color:"black", backgroundColor:"lightgray" }} edge="start" aria-label="menu" aria-haspopup="true" onClick={handleClick}>
             Choose Bottle Size
            </Button> */}
            <Typography  style={ {fontWeight:"bold"}} variant="body1" gutterBottom>
                  choose Size
            </Typography>
              <TextField
                id="bottle_size"
                select
                // label="Category"
                onChange={handleChangeSize}
                /* defaultValue={item_attributes[0].id} */
                name="bottle_size"
                inputRef={register}
                SelectProps={{
                native: true,
                }}
                helperText=""
                variant="outlined"
              >
                  {item_attributes.map((option) => {
                    return (
                    <option key={option.id} value={option.id}>
                      {option.bottle_size}
                    </option>
                    )}
                  )}
              </TextField>

              {is_offer ? 
              <Typography  style={ {fontWeight:"bold"}} variant="body1" gutterBottom>

              Price: <div className="offer">{price*rate} LBP</div> 
               <br></br>
              Offer Price: {offer*rate} LBP
              </Typography> : <Typography  style={ {fontWeight:"bold"}} variant="body1" gutterBottom>
                  Price: {price*rate} LBP
              </Typography>}





        </div>
         <Button className="buttonn"  style={{ alignSelf:"center", width:"50%", color:"black", backgroundColor:"lightgray" }} edge="start" aria-label="menu" aria-haspopup="true" onClick={() => onAdd()}>
             Add To Cart
        </Button>
       {/*  <div>
        <Basket
          cartItems={cartItems}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>
        </div> */}
        <div>
        <h2>Cart Items</h2>
      <div>
        {cartItems.length === 0 && <div>Cart is empty</div>}
        {cartItems.map((item) => (
          <div key={id} className="row">
            <div className="col-2">{name}</div>
            <div className="col-2">
              <button onClick={() => onRemove()} className="remove">
                -
              </button>{' '}
              <button onClick={() => onAdd()} className="add">
                +
              </button>
            </div>

            <div className="col-2 text-right">
              {item.qty} x LBP{price*rate}
            </div>
          </div>
        ))}

        {cartItems.length !== 0 && (
          <>
            <hr></hr>
            <div className="row">
              <div className="col-2">Items Price</div>
              <div className="col-1 text-right">$ {itemsPrice}</div>
            </div>
            <div className="row">
              <div className="col-2">Delivery Charge</div>
              <div className="col-1 text-right">
                LBP {shippingPrice}
              </div>
            </div>

            <div className="row">
              <div className="col-2">
                <strong>Total Price</strong>
              </div>
              <div className="col-1 text-right">
                <strong>$ {totalPrice}</strong>
              </div>
            </div>
            <hr />
            <div className="row">
              <button onClick={() => alert('Implement Checkout!')}>
                Checkout
              </button>
            </div>
            <div className="row">
              <button onClick={() => alert('Continue Shopping :) ')}>
                Continue Shopping
              </button>
            </div>
          </>
        )}
      </div>
        </div>

            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
}
