import React, { Component, Fragment, useState } from 'react';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from './store/actions';
import asyncComponent from './hoc/asyncComponent';

// COMPONENTS
import Layout from './layout/Layout';
import Cart from './containers/Cart/Cart';
import Contact from './containers/Contact/Contact';
import Details from './containers/Details/Details';
import Product from './containers/ProductList/Product';

import Offers from './containers/OfferList/Offers';
import Featured from './containers/FeaturedList/Featured';

import Wishlist from './containers/Wishlist/Wishlist';
import HomePage from './containers/HomePage/HomePage';
import Logout from './containers/Auth/Logout/Logout';
// admin
import Protection from './Dashboard/loginPages/Protection';
import Dashboard from './Dashboard/Dashboard';
import NotFound from './Dashboard/Pages/Not_Found/NotFound'
import Login from './Dashboard/loginPages/login.js';

const asyncOrders = asyncComponent(() => {
  return import('./containers/Orders/Orders');
});

const asyncAuth = asyncComponent(() => {
  return import('./containers/Auth/Auth');
});

/* const { pic, setpic } = useState(""); */

 /*  const picsetting = (pic) => {
    setpic(pic);
  };
 */
class App extends Component {
  componentDidMount() {
    this.props.onTryAutoSignup();
  };

  render() {
    const { isAuth } = this.props;

    return (
      <Fragment>

          <Switch>
            <Protection path="/admin" >
                <Dashboard /* pic={pic} */ />
            </Protection>
                <Route  path='/kataleya-admin'>
                  <Login /* picsetting={picsetting} */ />
                </Route>
                {/* <Route>
                   <NotFound />
                </Route> */}
            <Layout>
            {!isAuth && <Route path="/auth" component={asyncAuth} />}
            {isAuth && <Route path="/orders" component={asyncOrders} />}
            {isAuth && <Route path="/logout" component={Logout} />}
            <Route path="/cart" component={Cart} />
            <Route path="/contact" component={Contact} />
            <Route path="/details/:id" component={Details} />
            <Route path="/productlist" component={Product} />
            <Route path="/offers" component={Offers} />
            <Route path="/featured" component={Featured} />
         {/*    <Route path="/wishlist" component={Wishlist} /> */}
            <Route path="/" exact component={HomePage} />
            <Redirect to="/" />
            </Layout>
          </Switch>

      </Fragment>
    );
  }
};

const mapStateToProps = state => {
  return {
    isAuth: state.auth.token !== null
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onTryAutoSignup: () => dispatch(actions.authCheckState())
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
